import puppy10 from './images/puppy10.jpg';
import puppy11 from './images/puppy11.jpeg';
import puppy12 from './images/puppy12.jpg';

import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component {
  constructor() {
    //you must call the Component constructor function using super(), before setting any properties in this class - this is a react standard
    super();

    //this is a react standard, you must call this.state
    this.state = {
      score : 0,
      topscore: 0,
    }

  }

  checkImage = (event) => {
    this.setState({
      score: this.state.score + 1
    }) 
  }

  updateAnotherName = (event) => {
    this.setState({
      anotherName: event.target.value
    })
  }

  /*  
    part one

    put a key inside the state called num and set it to 3 and display that num in the jsx

    part two

    create a button and when you click on it, the num goes up by 1

  */

  //this function must be called render
