      <div className="App">

        <h2>Score: {this.state.score}</h2>
        <h2>Top Score: {this.state.topscore}</h2>

        <img src={puppy1} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy2} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy3} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy4} className="App-logo" alt="logo" onClick={this.checkImage} />
