        <img src={puppy7} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy8} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy9} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy10} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy11} className="App-logo" alt="logo" onClick={this.checkImage} />
        <img src={puppy12} className="App-logo" alt="logo" onClick={this.checkImage} />

        {/*<Person/>*/}

        {/*<Friend friend_name="human" />
        <Friend friend_name="Irina" />
        <Friend friend_name="Melinda" />
        <Friend friend_name="Arashi" />
        <Friend friend_name="Jacob" />*/}

        {/*
        <input type="text" onChange={this.updateName} placeholder="update name"/>

        <input type="text" onChange={this.updateAnotherName} placeholder="update another name"/>

        <button onClick={this.increaseNum}>click me</button>

        <br /><br />
        {this.state.num}
        <br /><br />

        {this.state.anotherName}
        {this.state.anotherName}
        {this.state.anotherName}
        --------

        {this.state.anotherName.split('').reverse().join('')}

        --------

        {this.state.name}
        {this.state.num}

        --------

        {this.state.name.split('').reverse().join('')}*/}
      </div>
    );
  }
}

export default App;
